"use strict";

var fluid = require("infusion"),
    fs = require("fs"),
    path = require("path");

var gpii = fluid.registerNamespace("gpii");

// The USB user listener.
fluid.defaults("gpii.userListeners.usb", {
    gradeNames: ["fluid.component", "fluid.contextAware", "gpii.userListener"],
    contextAwareness: {
        platform: {
            checks: {
                windows: {
                    contextValue: "{gpii.contexts.windows}",
                    gradeNames: "gpii.userListeners.usb.windows"
                },
                linux: {
                    contextValue: "{gpii.contexts.linux}",
                    gradeNames: "gpii.userListeners.usb.linux"
                }
            }
        }
    },
    events: {
        // A volume has been mounted
        onMount: null, // (that, mountPoint)
        // A volume has been unmounted
        onUnmount: null // (that, mountPoint)
    },
    members: {
        gpiiKeyFile: ".gpii-user-gpiiKey.txt",
        currentDevice: {
            gpiiKey: null,
            path: null
        }
    },
    listeners: {
        "onMount.readGpiiKey": "{that}.readUSBGpiiKey({that}, {arguments}.1)",
        "onUnmount.deviceRemoved": "{that}.deviceRemoved({that}, {arguments}.1)"
    },
    invokers: {
        startListener: "fluid.identity",
        stopListener: "fluid.identity",
        readUSBGpiiKey: {
            funcName: "gpii.userListeners.usb.readUSBGpiiKey",
            args: ["{that}", "{arguments}.1"]
        },
        deviceRemoved: {
            funcName: "gpii.userListeners.usb.deviceRemoved",
            args: ["{that}", "{arguments}.1"]
        }
    }
});

/**
 * Reads a GPII key from the USB device. If one is found, the onGpiiKeyArrive event is raised.
 *
 * If there's already a device with a gpiiKey, then the new one takes precedence. See GPII-2682.
 *
 * @param {Component} that - The gpii.userListeners.usb instance.
 * @param {String} usbPath - The root of the USB device.
 * @return {Promise} resolves when the GPII key is read, rejects on error (or there's already a device)
 */
gpii.userListeners.usb.readUSBGpiiKey = function (that, usbPath) {
    var promise = fluid.promise();

    // Try to read the GPII key file.
    var gpiiKeyFile = path.join(usbPath, that.gpiiKeyFile);
    fs.readFile(gpiiKeyFile, "utf8", function (err, data) {
        if (err) {
            fluid.log("Error reading GPII key from device (" + usbPath + "):");
            promise.reject({
                isError: true,
                message: "Error reading GPII key from device (" + usbPath + ")",
                error: err
            });
        } else {
            that.currentDevice.path = usbPath;
            fluid.log("Got GPII key from USB device (" + that.currentDevice.path + "):", that.currentDevice.gpiiKey);
            if (that.currentDevice.gpiiKey !== data.trim()) {
                that.currentDevice.gpiiKey = data.trim();
                that.events.onGpiiKeyArrive.fire(that, that.currentDevice.gpiiKey);
            } else {
                fluid.log("Ignoring key-in request. Got the same GPII key from a different USB device (" + that.currentDevice.path + "):",
                    that.currentDevice.gpiiKey);
            }
            promise.resolve(that.currentDevice.gpiiKey);
        }
    });

    return promise;
};

/**
 * Called when a device has been removed. If the device had a gpiiKey, then the onGpiiKeyRemove event is raised.
 *
 * @param {Component} that - The gpii.userListeners.usb instance.
 * @param {String} usbPath - The root of the USB device.
 * @return {Boolean} True if the device had a GPII key that was used to login.
 */
gpii.userListeners.usb.deviceRemoved = function (that, usbPath) {
    var wasActive = that.currentDevice.path === usbPath;
    if (wasActive) {
        that.events.onGpiiKeyRemove.fire(that, that.currentDevice.gpiiKey);
        that.currentDevice.gpiiKey = that.currentDevice.path = null;
    }
    return wasActive;
};
